import React,{useState} from 'react';

import MoviesList from './components/MoviesList';
import './App.css';

function App() {
  const [movie,setMovie]=useState([]);
  const [isLoading,setIsLoading]=useState(false);
  const [error,setError]=useState(null);
  async function dataHandler(){
    setIsLoading(true);
    setError(null)
    try{
      const response=await fetch("https://swapi.dev/api/fil/");
      if(!response.ok){
        throw new Error('Something Went Wrong...Retry!!!');
      }
    const data=await response.json();
    const tranformedMovies=data.results.map((movies)=>{
      return {
        id:movies.episode_id,
        title:movies.title,
        openingText:movies.opening_crawl,
        releaseDate:movies.release_date
      }
    })
    setMovie(tranformedMovies);
    setIsLoading(false);

    }
    catch(error){
      console.log(err)
      setError(error.message);
    }

}


  return (
    <React.Fragment>
      <section>
        <button onClick={dataHandler}>Fetch Movies</button>
      </section>
      <section>
        {!isLoading && movie.length==0 && <p>No Movies found...</p>}
        {!isLoading && movie.length>0 && <MoviesList movies={movie} />}
        {isLoading && <p>Loading...</p>}
        {!isLoading && error && <p>{error}</p>}
        </section>
    </React.Fragment>
  );
}

export default App;
